mapper.select:
SELECT
mi.n_id_infraccion AS id_infraccion, mi.n_id_estado_tabla AS id_estado_tabla, mi.n_id_tipo_infraccion AS id_tipo_infraccion, mi.n_id_persona AS id_persona, mi.v_descripcion AS descripcion, mp.v_num_documento AS num_documento, mp.v_app_paterno AS app_paterno, mp.v_app_materno AS app_materno, mp.v_nombre AS nombre, mmd.v_descripcion_Corta AS infraccion, met.v_descripcion AS estado, CAST(mi.d_fecha AS DATE) AS fecha, F_TIPO_PERSONA(mp.v_num_documento) AS tipo_persona, mae.v_nombre AS area_estudio
FROM mov_infraccion mi LEFT JOIN mae_persona mp ON mi.n_id_persona = mp.n_id_persona LEFT JOIN mae_estado_tabla met ON mi.n_id_estado_tabla=met.n_id_estado_tabla LEFT JOIN mae_multi_tab_det mmd ON mi.n_id_tipo_infraccion=mmd.n_id_item
LEFT JOIN seg_usuario su ON su.v_username = mi.v_usuario_registro LEFT JOIN mae_area_administrativo mad ON su.n_id_persona = mad.n_id_administrativo LEFT JOIN mae_area_estudio mae ON mad.n_id_area_estudio = mae.n_id_area_estudio
mmd.n_id_item = #{tipoInfraccion}
AND met.n_id_estado_tabla = #{tipoEstado}
AND (mp.v_num_documento = #{numeroDocumento} OR mp.v_num_documento =F_CODIGO_PERSONA(#{numeroDocumento}))
AND mae.n_id_area_estudio = #{areaEstudio}
AND F_TIPO_PERSONA(mp.v_num_documento) = #{tipoPersona}
AND F_TIPO_PERSONA(mp.v_num_documento) = #{tipoPersona}
AND F_TIPO_PERSONA(mp.v_num_documento) = #{tipoPersona}
AND (mi.d_fecha BETWEEN #{fechaInicio} AND #{fechaFin})
AND ((mi.d_fecha BETWEEN (F_GENERADOR_FECHAS('INI', 'SEMANA',#{anioInicio} ,null, null, #{semanaInicio} )) AND (F_GENERADOR_FECHAS('FIN', 'SEMANA',#{anioFin} ,null, null, #{semanaFin} ))))
AND (mi.d_fecha BETWEEN (F_GENERADOR_FECHAS('INI', 'MES',#{anioInicio} ,#{mesInicio}, null, null )) AND (F_GENERADOR_FECHAS('FIN', 'MES',#{anioFin} ,#{mesFin}, null,null )))
(mi.d_fecha BETWEEN (F_GENERADOR_FECHAS('INI', 'ANIO',#{anioInicio} ,null, null, null )) AND (F_GENERADOR_FECHAS('FIN', 'ANIO',#{anioFin} ,null, null,null )))
